mapper.update:
UPDATE reservation SET
re_no=#{re_no}, lo_no=#{lo_no}, lo_name=#{lo_name}, type=#{type}, tr_name=#{tr_name}, RESTIME=#{restime}, user_name=#{user_name}, user_pw=#{user_pw}, user_tel=#{user_tel}, content=#{content}
WHERE re_no = #{re_no}

mapper.select:

select * from (select rownum rnum, t1.* 
 from (select * from reservation WHERE lo_no = #{locationNo}
) t1) 
 where rnum >= #{startList} and rnum <= #{endList}


select * from (select rownum rnum, t1.* 
 from (select * from restime WHERE lo_no = #{locationNo}
 ) t1) 
 where rnum >= #{startList} and rnum <= #{endList}


select * from (select rownum rnum, t1.* 
 from (select * from reservation WHERE lo_no = #{locationNo} 
 and ${keyField} like '%' || #{keyWord} || '%'
) t1) 
 where rnum >= #{startList} and rnum <= #{endList}


select * from (select rownum rnum, t1.* 
 from (select * from restime WHERE lo_no = #{locationNo} 
 and ${keyField} like '%' || #{keyWord} || '%'
) t1) 
 where rnum >= #{startList} and rnum <= #{endList}
