mapper.select:

select * from (select rownum rnum, t1.* 
 from (select * from PAYMENT WHERE lo_no = #{locationNo}
 order by PAYMENT_DATE DESC) t1) 
 where rnum >= #{startList} and rnum <= #{endList}


select * from (select rownum rnum, t1.* 
 from (select * from PAYMENT WHERE user_no = #{user_no}
 order by PAYMENT_DATE DESC) t1) 
 where rnum >= #{startList} and rnum <= #{endList}
