mapper.select:

select * from (select rownum rnum, t1.* from 
		(select * from review order by review_no DESC) t1) 
		 where rnum >= #{start} and rnum <= #{end}

mapper.update:
update review
set
title = #{title}, content =  #{content}, review_file_name = #{review_file_name}, tr_no = #{tr_no}, lo_no = #{lo_no},  starCnt = #{starCnt}
where  review_no = #{review_no}
